package com.thalhamer.numbersgame.Factory;import android.app.Application;import android.content.Context;import com.google.android.gms.ads.InterstitialAd;/** * Created by Brian on 2/7/2015. */public class App extends Application {    private static Context mContext;    private static Factory factory;    private static boolean connectedToPurchasingNetwork = false;    private static InterstitialAd mInterstitialAd;    private static boolean okToShowGameExplain = false;    @Override    public void onCreate() {        super.onCreate();        mContext = this;    }    public static Context getContext() {        return mContext;    }    public static Factory getFactory() {        return factory;    }    public static void setFactory(Factory factory) {        App.factory = factory;    }    public static boolean isConnectedToPurchasingNetwork() {        return connectedToPurchasingNetwork;    }    public static void setConnectedToPurchasingNetwork(boolean connectedToPurchasingNetwork) {        App.connectedToPurchasingNetwork = connectedToPurchasingNetwork;    }    public static InterstitialAd getmInterstitialAd() {        return mInterstitialAd;    }    public static void setmInterstitialAd(InterstitialAd mInterstitialAd) {        App.mInterstitialAd = mInterstitialAd;    }    public static boolean isOkToShowGameExplain() {        return okToShowGameExplain;    }    public static void setOkToShowGameExplain(boolean okToShowGameExplain) {        App.okToShowGameExplain = okToShowGameExplain;    }}