package com.thalhamer.numbersgame.Factory;import android.app.Application;import android.content.Context;import android.graphics.Point;import android.view.Display;import android.view.WindowManager;import com.google.android.gms.ads.InterstitialAd;import com.thalhamer.numbersgame.Modules.AppModule;import dagger.ObjectGraph;/** * App * * Created by Brian on 2/7/2015. */public class App extends Application {    private static Context mContext;    private static ObjectGraph mObjectGraph;    private static boolean connectedToPurchasingNetwork = false;    private static InterstitialAd mInterstitialAd;    private static ObjectGraph gameActivityObjectGraph;    private static Point screenDimensions;    @Override    public void onCreate() {        super.onCreate();        mContext = this;        mObjectGraph = ObjectGraph.create(new AppModule());        WindowManager wm = (WindowManager) getSystemService(Context.WINDOW_SERVICE);        Display display = wm.getDefaultDisplay();        Point screenDimensions = new Point();        display.getSize(screenDimensions);        this.screenDimensions = screenDimensions;    }    public static Context getContext() {        return mContext;    }    public static ObjectGraph getmObjectGraph() {        return mObjectGraph;    }    public static void setmObjectGraph(ObjectGraph mObjectGraph) {        App.mObjectGraph = mObjectGraph;    }    public static boolean isConnectedToPurchasingNetwork() {        return connectedToPurchasingNetwork;    }    public static void setConnectedToPurchasingNetwork(boolean connectedToPurchasingNetwork) {        App.connectedToPurchasingNetwork = connectedToPurchasingNetwork;    }    public static InterstitialAd getmInterstitialAd() {        return mInterstitialAd;    }    public static void setmInterstitialAd(InterstitialAd mInterstitialAd) {        App.mInterstitialAd = mInterstitialAd;    }    public static ObjectGraph getGameActivityObjectGraph() {        return gameActivityObjectGraph;    }    public static void setGameActivityObjectGraph(ObjectGraph gameActivityObjectGraph) {        App.gameActivityObjectGraph = gameActivityObjectGraph;    }    public static int getDisplayWidth() {        return screenDimensions.x;    }    public static int getDisplayHeight() {        return screenDimensions.y;    }}