package com.thalhamer.numbersgame.Factory;import android.app.Application;import android.content.Context;import com.google.android.gms.ads.InterstitialAd;import dagger.ObjectGraph;/** * App * * Created by Brian on 2/7/2015. */public class App extends Application {    private static Context mContext;    private static ObjectGraph objectGraph;    private static boolean connectedToPurchasingNetwork = false;    private static InterstitialAd mInterstitialAd;    private static boolean adClosed = false;    @Override    public void onCreate() {        super.onCreate();        mContext = this;    }    public static Context getContext() {        return mContext;    }    public static ObjectGraph getObjectGraph() {        return objectGraph;    }    public static void setObjectGraph(ObjectGraph objectGraph) {        App.objectGraph = objectGraph;    }    public static boolean isConnectedToPurchasingNetwork() {        return connectedToPurchasingNetwork;    }    public static void setConnectedToPurchasingNetwork(boolean connectedToPurchasingNetwork) {        App.connectedToPurchasingNetwork = connectedToPurchasingNetwork;    }    public static InterstitialAd getmInterstitialAd() {        return mInterstitialAd;    }    public static void setmInterstitialAd(InterstitialAd mInterstitialAd) {        App.mInterstitialAd = mInterstitialAd;    }    public static boolean isAdClosed() {        return adClosed;    }    public static void setAdClosed(boolean adClosed) {        App.adClosed = adClosed;    }}