package com.thalhamer.numbersgame.Fragment;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.TextView;import com.thalhamer.numbersgame.Activity.ChooseEpicActivity;import com.thalhamer.numbersgame.R;import com.thalhamer.numbersgame.domain.SectionUnlock;import com.thalhamer.numbersgame.enums.Character;import com.thalhamer.numbersgame.services.SavedDataService;import com.thalhamer.numbersgame.services.SectionUnlockService;/** * epic fragment for epic screen * <p/> * Created by Brian on 5/5/2015. */public class EpicFragment extends Fragment {    public static final String FACE_RESOURCE_ID = "imageRvalue";    public static final String PLACE_IN_LIST = "placeInList";    public static final String PERSON_NAME = "personName";    public static SavedDataService savedDataService;    public static ChooseEpicActivity chooseEpicActivity;    public static SectionUnlockService sectionUnlockService;    public static EpicFragment newInstance(Character character) {        EpicFragment f = new EpicFragment();        Bundle bdl = new Bundle(3);        bdl.putInt(FACE_RESOURCE_ID, character.getFaceResourceId());        bdl.putInt(PLACE_IN_LIST, character.getEpic());        bdl.putString(PERSON_NAME, character.getName());        f.setArguments(bdl);        return f;    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        Bundle bundle = getArguments();        View view = inflater.inflate(R.layout.fragment_choose_epic, container, false);        ImageButton imageButton = (ImageButton) view.findViewById(R.id.imageButton);        TextView personName = (TextView) view.findViewById(R.id.personName);        TextView numOfStars = (TextView) view.findViewById(R.id.numOfStars);        ImageView star = (ImageView) view.findViewById(R.id.star);        personName.setText(bundle.getString(PERSON_NAME));        Integer epic = bundle.getInt(PLACE_IN_LIST);        SectionUnlock sectionUnlock = sectionUnlockService.getSectionUnlock(epic, 1);        if (sectionUnlock.getUnlocked() == 1) {            imageButton.setBackgroundResource(bundle.getInt(FACE_RESOURCE_ID));            imageButton.setTag(R.string.chosen_epic, epic.toString());            imageButton.setOnClickListener(chooseEpicActivity);            numOfStars.setVisibility(View.GONE);            star.setVisibility(View.GONE);        } else {            imageButton.setBackgroundResource(R.mipmap.padlock);            numOfStars.setText(sectionUnlock.getNumOfStars().toString());        }        return view;    }}