package com.thalhamer.numbersgame.Modules;import com.thalhamer.numbersgame.Activity.GameActivity;import com.thalhamer.numbersgame.Factory.Factory;import com.thalhamer.numbersgame.domain.GameDataHolder;import com.thalhamer.numbersgame.services.GameEndService;import com.thalhamer.numbersgame.services.GameInitiateService;import com.thalhamer.numbersgame.services.GridMappingService;import com.thalhamer.numbersgame.services.GridService;import com.thalhamer.numbersgame.services.GridTileDataService;import com.thalhamer.numbersgame.services.InAppPurchaseService;import com.thalhamer.numbersgame.services.JsonService;import com.thalhamer.numbersgame.services.MessageService;import com.thalhamer.numbersgame.services.MotionEventService;import com.thalhamer.numbersgame.services.PopupService;import com.thalhamer.numbersgame.services.PowerService;import com.thalhamer.numbersgame.services.SavedDataService;import com.thalhamer.numbersgame.services.SoundService;import com.thalhamer.numbersgame.services.StarsAndUnlockService;import com.thalhamer.numbersgame.services.StatsService;import com.thalhamer.numbersgame.services.TimerService;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;/** * Module * <p/> * Created by Brian on 4/8/2015. */@Module(injects = {        GridMappingService.class,        MotionEventService.class,        GameActivity.class,        Factory.class,        GameInitiateService.class,        GameEndService.class,        GridService.class,        StatsService.class,        TimerService.class,        PowerService.class,        StarsAndUnlockService.class,        MessageService.class,        PopupService.class,        InAppPurchaseService.class},        library = true)public class AppModule {    @Provides    @Singleton    public GameDataHolder provideGameDataHolder() {        return new GameDataHolder();    }    @Provides    @Singleton    public SavedDataService provideSavedDataService() {        return new SavedDataService();    }    @Provides    @Singleton    public JsonService provideJsonService() {        return new JsonService();    }    @Provides    @Singleton    public SoundService soundService() {        return new SoundService();    }    @Provides    @Singleton    public GridTileDataService gridTileDataService() {        return new GridTileDataService();    }}