package com.thalhamer.numbersgame.domain;import com.thalhamer.numbersgame.enums.sounds.Direction;/** * Created by Brian on 10/25/2015. */public class DiagonalTile {    Tile tile;    Direction direction;    public DiagonalTile(Tile tile, Direction direction) {        this.tile = tile;        this.direction = direction;    }    public Tile getTile() {        return tile;    }    public void setTile(Tile tile) {        this.tile = tile;    }    public Direction getDirection() {        return direction;    }    public void setDirection(Direction direction) {        this.direction = direction;    }}