package com.thalhamer.numbersgame.domain;import com.thalhamer.numbersgame.viewhelper.GameConstants;/** * Created by Brian on 4/21/2015. */public class DropBlockData {    private int periodBetweenFreqChanges;    private int initialPeriod;    private double periodChangeFactor;    private int numOfRowsToPopulate;    public DropBlockData(int periodBetweenFreqChanges, int initialPeriod, double periodChangeFactor, int numOfRowsToPopulate) {        this.periodBetweenFreqChanges = periodBetweenFreqChanges;        this.initialPeriod = initialPeriod;        this.periodChangeFactor = periodChangeFactor;        this.numOfRowsToPopulate = numOfRowsToPopulate;    }    public DropBlockData(int numOfRowsToPopulate) {        this.numOfRowsToPopulate = numOfRowsToPopulate;        this.initialPeriod = GameConstants.DROP_BLOCK_INITIAL_PERIOD_IN_SEC;        this.periodBetweenFreqChanges = GameConstants.DROP_BLOCK_TIME_BETWEEN_FREQ_CHANGES_IN_SEC;        this.periodChangeFactor = GameConstants.DROP_BLOCK_PERIOD_CHANGE_FACTOR;    }    public int getPeriodBetweenFreqChanges() {        return periodBetweenFreqChanges;    }    public void setPeriodBetweenFreqChanges(int periodBetweenFreqChanges) {        this.periodBetweenFreqChanges = periodBetweenFreqChanges;    }    public int getInitialPeriod() {        return initialPeriod;    }    public void setInitialPeriod(int initialPeriod) {        this.initialPeriod = initialPeriod;    }    public double getPeriodChangeFactor() {        return periodChangeFactor;    }    public void setPeriodChangeFactor(double periodChangeFactor) {        this.periodChangeFactor = periodChangeFactor;    }    public int getNumOfRowsToPopulate() {        return numOfRowsToPopulate;    }    public void setNumOfRowsToPopulate(int numOfRowsToPopulate) {        this.numOfRowsToPopulate = numOfRowsToPopulate;    }}