package com.thalhamer.numbersgame.domain;/** * Floating point display on game activity * <p/> * Created by Brian on 2/3/2015. */public class FloatingPointsDisplay {    private Long points;    private Float xPixelLoc;    private Float yPixelLoc;    public FloatingPointsDisplay(Long points, Float xPixelLoc, Float yPixelLoc) {        this.points = points;        this.xPixelLoc = xPixelLoc;        this.yPixelLoc = yPixelLoc;    }    public Long getPoints() {        return points;    }    public void setPoints(Long points) {        this.points = points;    }    public Float getxPixelLoc() {        return xPixelLoc;    }    public void setxPixelLoc(Float xPixelLoc) {        this.xPixelLoc = xPixelLoc;    }    public Float getyPixelLoc() {        return yPixelLoc;    }    public void setyPixelLoc(Float yPixelLoc) {        this.yPixelLoc = yPixelLoc;    }}