package com.thalhamer.numbersgame.domain;import android.graphics.Paint;import android.graphics.RectF;/** * Game acivity data box * <p/> * Created by Brian on 8/5/2015. */public class GameActivityDataBox {    private RectF boxRectF;    private Paint boxPaintFill;    private Paint boxPaintBorder;    private Paint labelPaint;    private Float labelLeft;    private Float labelTop;    private String labelText;    private Paint valuePaint;    private Float valueLeft;    private Float valueTop;    public RectF getBoxRectF() {        return boxRectF;    }    public void setBoxRectF(RectF boxRectF) {        this.boxRectF = boxRectF;    }    public Paint getBoxPaintFill() {        return boxPaintFill;    }    public void setBoxPaintFill(Paint boxPaintFill) {        this.boxPaintFill = boxPaintFill;    }    public Paint getBoxPaintBorder() {        return boxPaintBorder;    }    public void setBoxPaintBorder(Paint boxPaintBorder) {        this.boxPaintBorder = boxPaintBorder;    }    public Paint getLabelPaint() {        return labelPaint;    }    public void setLabelPaint(Paint labelPaint) {        this.labelPaint = labelPaint;    }    public Paint getValuePaint() {        return valuePaint;    }    public void setValuePaint(Paint valuePaint) {        this.valuePaint = valuePaint;    }    public Float getLabelLeft() {        return labelLeft;    }    public void setLabelLeft(Float labelLeft) {        this.labelLeft = labelLeft;    }    public Float getLabelTop() {        return labelTop;    }    public void setLabelTop(Float labelTop) {        this.labelTop = labelTop;    }    public Float getValueLeft() {        return valueLeft;    }    public void setValueLeft(Float valueLeft) {        this.valueLeft = valueLeft;    }    public Float getValueTop() {        return valueTop;    }    public void setValueTop(Float valueTop) {        this.valueTop = valueTop;    }    public String getLabelText() {        return labelText;    }    public void setLabelText(String labelText) {        this.labelText = labelText;    }}