package com.thalhamer.numbersgame.domain;import android.graphics.RectF;import com.thalhamer.numbersgame.enums.Character;import com.thalhamer.numbersgame.enums.NumTile;import com.thalhamer.numbersgame.enums.SpecialTile;/** * Image * <p/> * Created by Brian on 1/29/2015. */public class Image {    private NumTile numTile;    private Character character;    private SpecialTile specialTile;    private RectF rectF;    private Integer placesToDrop = 0;    private boolean newImage = false;    private Float distanceDropped;    private Float distanceToDrop;    public Image(NumTile numTile) {        this.numTile = numTile;    }    public Image(Character character) {        this.character = character;    }    public Image(SpecialTile specialTile) {        this.specialTile = specialTile;    }    public RectF getRectF() {        return rectF;    }    public void setRectF(RectF rectF) {        this.rectF = rectF;    }    public Integer getPlacesToDrop() {        return placesToDrop;    }    public void setPlacesToDrop(Integer placesToDrop) {        this.placesToDrop = placesToDrop;    }    public NumTile getNumTile() {        return numTile;    }    public void setNumTile(NumTile numTile) {        this.numTile = numTile;    }    public Character getCharacter() {        return character;    }    public void setCharacter(Character character) {        this.character = character;    }    public Float getDistanceDropped() {        return distanceDropped;    }    public void setDistanceDropped(Float distanceDropped) {        this.distanceDropped = distanceDropped;    }    public Float getDistanceToDrop() {        return distanceToDrop;    }    public void setDistanceToDrop(Float distanceToDrop) {        this.distanceToDrop = distanceToDrop;    }    public boolean isNewImage() {        return newImage;    }    public void setNewImage(boolean newImage) {        this.newImage = newImage;    }    public SpecialTile getSpecialTile() {        return specialTile;    }    public void setSpecialTile(SpecialTile specialTile) {        this.specialTile = specialTile;    }}