package com.thalhamer.numbersgame.domain;import com.thalhamer.numbersgame.enums.CalcType;import com.thalhamer.numbersgame.enums.GameType;import com.thalhamer.numbersgame.enums.OperType;import com.thalhamer.numbersgame.enums.ScoreType;import java.util.List;/** * Level info * <p/> * Created by Brian on 1/18/2015. */public class LevelInfo {    private GameType gameType;    private CalcType calcType;    private ScoreType scoreType;    private GridData gridData;    private Stats stats;    private DropBlockData dropBlockData;    private List<Power> powers;    private Long scoreTypeValue;    private List<OperType> allowedOperations;    private Integer[][] gridTileData;    public LevelInfo(GameType gameType, GridData gridData, CalcType calcType, Stats stats, ScoreType scoreType) {        this.gameType = gameType;        this.gridData = gridData;        this.stats = stats;        this.calcType = calcType;        this.scoreType = scoreType;    }    public GridData getGridData() {        return gridData;    }    public void setGridData(GridData gridData) {        this.gridData = gridData;    }    public Stats getStats() {        return stats;    }    public void setStats(Stats stats) {        this.stats = stats;    }    public GameType getGameType() {        return gameType;    }    public void setGameType(GameType gameType) {        this.gameType = gameType;    }    public DropBlockData getDropBlockData() {        return dropBlockData;    }    public void setDropBlockData(DropBlockData dropBlockData) {        this.dropBlockData = dropBlockData;    }    public List<Power> getPowers() {        return powers;    }    public void setPowers(List<Power> powers) {        this.powers = powers;    }    public List<OperType> getAllowedOperations() {        return allowedOperations;    }    public void setAllowedOperations(List<OperType> allowedOperations) {        this.allowedOperations = allowedOperations;    }    public CalcType getCalcType() {        return calcType;    }    public void setCalcType(CalcType calcType) {        this.calcType = calcType;    }    public ScoreType getScoreType() {        return scoreType;    }    public void setScoreType(ScoreType scoreType) {        this.scoreType = scoreType;    }    public Long getScoreTypeValue() {        return scoreTypeValue;    }    public void setScoreTypeValue(Long scoreTypeValue) {        this.scoreTypeValue = scoreTypeValue;    }    public Integer[][] getGridTileData() {        return gridTileData;    }    public void setGridTileData(Integer[][] gridTileData) {        this.gridTileData = gridTileData;    }}