package com.thalhamer.numbersgame.domain;import android.graphics.Paint;import android.graphics.RectF;/** * Star meter on game activity * <p/> * Created by Brian on 8/2/2015. */public class StarMeter {    private RectF frameRectF;    private Paint framePaint;    private RectF fillRectF;    private Paint fillPaint;    private Paint backgroundPaint;    private Paint linePaint;    private float pixelPerScore;    public RectF getFrameRectF() {        return frameRectF;    }    public void setFrameRectF(RectF frameRectF) {        this.frameRectF = frameRectF;    }    public Paint getFramePaint() {        return framePaint;    }    public void setFramePaint(Paint framePaint) {        this.framePaint = framePaint;    }    public RectF getFillRectF() {        return fillRectF;    }    public void setFillRectF(RectF fillRectF) {        this.fillRectF = fillRectF;    }    public Paint getFillPaint() {        return fillPaint;    }    public void setFillPaint(Paint fillPaint) {        this.fillPaint = fillPaint;    }    public float getPixelPerScore() {        return pixelPerScore;    }    public void setPixelPerScore(float pixelPerScore) {        this.pixelPerScore = pixelPerScore;    }    public Paint getLinePaint() {        return linePaint;    }    public void setLinePaint(Paint linePaint) {        this.linePaint = linePaint;    }    public Paint getBackgroundPaint() {        return backgroundPaint;    }    public void setBackgroundPaint(Paint backgroundPaint) {        this.backgroundPaint = backgroundPaint;    }}