package com.thalhamer.numbersgame.domain;/** * Created by Brian on 5/7/2015. */public class StarsInfo {    private Integer currentNumOfStars;    private Long minForOneStar;    private Long minForTwoStars;    private Long minForThreeStars;    public Integer getCurrentNumOfStars() {        return currentNumOfStars;    }    public void setCurrentNumOfStars(Integer currentNumOfStars) {        this.currentNumOfStars = currentNumOfStars;    }    public Long getMinForOneStar() {        return minForOneStar;    }    public void setMinForOneStar(Long minForOneStar) {        this.minForOneStar = minForOneStar;    }    public Long getMinForTwoStars() {        return minForTwoStars;    }    public void setMinForTwoStars(Long minForTwoStars) {        this.minForTwoStars = minForTwoStars;    }    public Long getMinForThreeStars() {        return minForThreeStars;    }    public void setMinForThreeStars(Long minForThreeStars) {        this.minForThreeStars = minForThreeStars;    }}