package com.thalhamer.numbersgame.domain;/** * Stats * <p/> * Created by Brian on 2/1/2015. */public class Stats {    private Long score = 0L;    private Integer numOfMovesLeft;    private Long numOfMillisLeft;    private FloatingPointsDisplay floatingPointsDisplay;    private Long timeElapsed = 0L;    private StarsInfo starsInfo;    public Long getScore() {        return score;    }    public void setScore(Long score) {        this.score = score;    }    public Integer getNumOfMovesLeft() {        return numOfMovesLeft;    }    public void setNumOfMovesLeft(Integer numOfMovesLeft) {        this.numOfMovesLeft = numOfMovesLeft;    }    public FloatingPointsDisplay getFloatingPointsDisplay() {        return floatingPointsDisplay;    }    public void setFloatingPointsDisplay(FloatingPointsDisplay floatingPointsDisplay) {        this.floatingPointsDisplay = floatingPointsDisplay;    }    public Long getNumOfMillisLeft() {        return numOfMillisLeft;    }    public void setNumOfMillisLeft(Long numOfMillisLeft) {        this.numOfMillisLeft = numOfMillisLeft;    }    public Long getTimeElapsed() {        return timeElapsed;    }    public void setTimeElapsed(Long timeElapsed) {        this.timeElapsed = timeElapsed;    }    public StarsInfo getStarsInfo() {        return starsInfo;    }    public void setStarsInfo(StarsInfo starsInfo) {        this.starsInfo = starsInfo;    }}