package com.thalhamer.numbersgame.domain;import android.graphics.RectF;import com.thalhamer.numbersgame.enums.TileAttribute;/** * Tile * <p/> * Created by Brian on 1/18/2015. */public class Tile {    private Integer colNum;    private Integer rowNum;    private Image image;    private boolean touched;    private OperationImage leftOperationImage;    private OperationImage topOperationImage;    private RectF rectF;    private TileAttribute tileAttribute;    private RectF explosionRectF;    public Tile(Integer colNum, Integer rowNum) {        this.colNum = colNum;        this.rowNum = rowNum;    }    public Tile(Integer colNum, Integer rowNum, Image image) {        this.colNum = colNum;        this.rowNum = rowNum;        this.image = image;    }    public Integer getColNum() {        return colNum;    }    public void setColNum(Integer colNum) {        this.colNum = colNum;    }    public Integer getRowNum() {        return rowNum;    }    public void setRowNum(Integer rowNum) {        this.rowNum = rowNum;    }    public Image getImage() {        return image;    }    public void setImage(Image image) {        this.image = image;    }    public boolean isTouched() {        return touched;    }    public void setTouched(boolean touched) {        this.touched = touched;    }    public OperationImage getLeftOperationImage() {        return leftOperationImage;    }    public void setLeftOperationImage(OperationImage leftOperationImage) {        this.leftOperationImage = leftOperationImage;    }    public OperationImage getTopOperationImage() {        return topOperationImage;    }    public void setTopOperationImage(OperationImage topOperationImage) {        this.topOperationImage = topOperationImage;    }    public RectF getRectF() {        return rectF;    }    public void setRectF(RectF rectF) {        this.rectF = rectF;    }    public TileAttribute getTileAttribute() {        return tileAttribute;    }    public void setTileAttribute(TileAttribute tileAttribute) {        this.tileAttribute = tileAttribute;    }    public RectF getExplosionRectF() {        return explosionRectF;    }    public void setExplosionRectF(RectF explosionRectF) {        this.explosionRectF = explosionRectF;    }    @Override    public String toString() {        StringBuilder sb = new StringBuilder();        sb.append("col: ").append(colNum).append(",row: ").append(rowNum).append(",imageVal: ");        if (image != null) {            if (image.getNumTile() != null) {                sb.append(image.getNumTile().value);            } else {                sb.append(image.getCharacter().getName());            }            sb.append(",placesToDrop: ").append(image.getPlacesToDrop());        } else {            sb.append("empty");        }        return sb.toString();    }}