package com.thalhamer.numbersgame.enums;import com.thalhamer.numbersgame.R;/** * Calculation type for game * <p/> * Created by Brian on 4/25/2015. */public enum CalcType {    ADD("Add", "Add - slide over adjacent blocks until adds up to specified value.", R.mipmap.explain_add),    SUBTRACT("Subtract", "Subtract - first block is the number you start with. Slide over adjacent blocks to subtract from this.", R.mipmap.explain_subtract),    MULTIPLY("Multiply", "Multiply - slide over adjacent blocks until multiplies to specified value.", R.mipmap.explain_multiply),    MULT_OPER("Calculate", "not included...", 5);    private String calculation;    private String description;    private int explanationId;    CalcType(String calculation, String description, int explanationId) {        this.calculation = calculation;        this.description = description;        this.explanationId = explanationId;    }    public String getCalculation() {        return calculation;    }    public String getDescription() {        return description;    }    public int getExplanationId() {        return explanationId;    }}