package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;/** * Created by Brian on 2/7/2015. */public enum NumTile {    ONE(1, R.drawable.tile_1), TWO(2, R.drawable.tile_2), THREE(3, R.drawable.tile_3), FOUR(4, R.drawable.tile_4),    FIVE(5, R.drawable.tile_5), SIX(6, R.drawable.tile_6), SEVEN(7, R.drawable.tile_7), EIGHT(8, R.drawable.tile_8),    NINE(9, R.drawable.tile_9);    public final int value;    public final Bitmap bitmap;    NumTile(int value, int imageNum) {        this.value = value;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), imageNum);    }    public static NumTile getGameTileFromValue(int value) {        for (NumTile numTile : NumTile.values()) {            if (numTile.value == value) {                return numTile;            }        }        return null;    }}