package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;import java.util.EnumSet;/** * Power * <p/> * Created by Brian on 4/24/2015. */public enum PowerEnum {    CLEAR_ONE_NUM(R.drawable.tile_power_single, "power_clear_tile_single", R.drawable.buy_tab1, "Single Tile Clear"),    CLEAR_ALL_NUM(R.drawable.tile_power_multi, "power_clear_tile_multiple", R.drawable.buy_tab2, "Multiple Tile Clear"),    PAUSE_TIME(R.drawable.time_power, "power_time_stop", R.drawable.buy_tab3, "Time Stop");    private Bitmap bitmap;    private int imageResourceId;    private String appPurchaseSKU;    private int tabImageId;    private String buyPowerTitle;    PowerEnum(int imageResourceId, String appPurchaseSKU, int tabImageId, String buyPowerTitle) {        this.imageResourceId = imageResourceId;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), imageResourceId);        this.appPurchaseSKU = appPurchaseSKU;        this.tabImageId = tabImageId;        this.buyPowerTitle = buyPowerTitle;    }    public static EnumSet<PowerEnum> multiUse() {        return EnumSet.of(PowerEnum.CLEAR_ONE_NUM, PowerEnum.CLEAR_ALL_NUM);    }    public static EnumSet<PowerEnum> singleUse() {        return EnumSet.of(PowerEnum.PAUSE_TIME);    }    public Bitmap getBitmap() {        return bitmap;    }    public int getImageResourceId() {        return imageResourceId;    }    public String getAppPurchaseSKU() {        return appPurchaseSKU;    }    public int getTabImageId() {        return tabImageId;    }    public String getBuyPowerTitle() {        return buyPowerTitle;    }    public static PowerEnum getPowerEnumByPurchaseSKU(String sku) {        for (PowerEnum powerEnum : PowerEnum.values()) {            if (sku.contains(powerEnum.getAppPurchaseSKU())) {                return powerEnum;            }        }        return null;    }}