package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;/** * Power * <p/> * Created by Brian on 4/24/2015. */public enum PowerEnum {    CLEAR_ONE_NUM(1, R.drawable.tile_power_single, "power_clear_tile_single", R.drawable.buy_tab1, "Single Tile Clear"),    CLEAR_ALL_NUM(2, R.drawable.tile_power_multi, "power_clear_tile_multiple", R.drawable.buy_tab2, "Multiple Tile Clear");    private int powerNum;    private Bitmap bitmap;    private int imageResourceId;    private String appPurchaseSKU;    private int tabImageId;    private String buyPowerTitle;    PowerEnum(int powerNum, int imageResourceId, String appPurchaseSKU, int tabImageId, String buyPowerTitle) {        this.imageResourceId = imageResourceId;        this.powerNum = powerNum;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), imageResourceId);        this.appPurchaseSKU = appPurchaseSKU;        this.tabImageId = tabImageId;        this.buyPowerTitle = buyPowerTitle;    }    public int getPowerNum() {        return powerNum;    }    public Bitmap getBitmap() {        return bitmap;    }    public int getImageResourceId() {        return imageResourceId;    }    public String getAppPurchaseSKU() {        return appPurchaseSKU;    }    public int getTabImageId() {        return tabImageId;    }    public String getBuyPowerTitle() {        return buyPowerTitle;    }}