package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;/** * Created by Brian on 10/27/2015. */public enum SpecialTile {    LEPRECHAUN(3, R.mipmap.leprechaun_head);    private int mapValue;    private int resourceId;    private Bitmap bitmap;    SpecialTile(int mapValue, int resourceId) {        this.mapValue = mapValue;        this.resourceId = resourceId;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), resourceId);    }    public static SpecialTile getSpecialTileByMappedValue(Integer value) {        for (SpecialTile specialTile : SpecialTile.values()) {            if (specialTile.mapValue == value) {                return specialTile;            }        }        return null;    }    public int getMapValue() {        return mapValue;    }    public int getResourceId() {        return resourceId;    }    public Bitmap getBitmap() {        return bitmap;    }}