package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;import java.util.EnumSet;/** * Created by Brian on 10/22/2015. */public enum TileAttribute {    BUBBLE1(5, R.mipmap.bubble1), BUBBLE2(6, R.mipmap.bubble2),    BLOCK1(10, R.mipmap.block1), BLOCK2(11, R.mipmap.block2);    private int mappedInteger;    private int resourceId;    private Bitmap bitmap;    TileAttribute(int mappedInteger, int resourceId) {        this.mappedInteger = mappedInteger;        this.resourceId = resourceId;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), resourceId);    }    public static TileAttribute getTileAttributeByMappedInteger(int mappedInteger) {        for (TileAttribute tileAttribute : TileAttribute.values()) {            if (tileAttribute.mappedInteger == mappedInteger) {                return tileAttribute;            }        }        return null;    }    public int getResourceId() {        return resourceId;    }    public Bitmap getBitmap() {        return bitmap;    }    public int getMappedInteger() {        return mappedInteger;    }    public static EnumSet<TileAttribute> noImageTileAttributes() {        return EnumSet.of(TileAttribute.BLOCK1, TileAttribute.BLOCK2);    }    public static EnumSet<TileAttribute> blockTileAttributes() {        return EnumSet.of(TileAttribute.BLOCK1, TileAttribute.BLOCK2);    }    public static EnumSet<TileAttribute> bubbleTileAttributes() {        return EnumSet.of(TileAttribute.BUBBLE1, TileAttribute.BUBBLE2);    }    public static TileAttribute reduceTileAttribute(TileAttribute tileAttribute) {        if (tileAttribute == null) {            return null;        }        if (tileAttribute.getMappedInteger() % 5 == 0) {            return null;        } else {            return getTileAttributeByMappedInteger(tileAttribute.getMappedInteger() - 1);        }    }}