package com.thalhamer.numbersgame.enums;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;import java.util.EnumSet;/** * Created by Brian on 10/22/2015. */public enum TileAttribute {    Glass1(5, R.mipmap.glass1), Glass2(6, R.mipmap.glass2),    BLOCK1(10, R.mipmap.block1), BLOCK2(11, R.mipmap.block2);    private static final String glassDescription = "Glass - get rid of by including tile in sequence with glass in front of it.  Note: Clear powers will break the glass.";    private static final String blockDescription = "Blocks - get rid of by including tile in sequence with block on adjacent tile.";    private int mappedInteger;    private int resourceId;    private Bitmap bitmap;    TileAttribute(int mappedInteger, int resourceId) {        this.mappedInteger = mappedInteger;        this.resourceId = resourceId;        this.bitmap = BitmapFactory.decodeResource(App.getContext().getResources(), resourceId);    }    public static TileAttribute getTileAttributeByMappedInteger(int mappedInteger) {        for (TileAttribute tileAttribute : TileAttribute.values()) {            if (tileAttribute.mappedInteger == mappedInteger) {                return tileAttribute;            }        }        return null;    }    public int getResourceId() {        return resourceId;    }    public Bitmap getBitmap() {        return bitmap;    }    public int getMappedInteger() {        return mappedInteger;    }    public static EnumSet<TileAttribute> noImageTileAttributes() {        return EnumSet.of(TileAttribute.BLOCK1, TileAttribute.BLOCK2);    }    public static EnumSet<TileAttribute> blockTileAttributes() {        return EnumSet.of(TileAttribute.BLOCK1, TileAttribute.BLOCK2);    }    public static EnumSet<TileAttribute> bubbleTileAttributes() {        return EnumSet.of(TileAttribute.Glass1, TileAttribute.Glass2);    }    public static TileAttribute reduceTileAttribute(TileAttribute tileAttribute) {        if (tileAttribute == null) {            return null;        }        if (tileAttribute.getMappedInteger() % 5 == 0) {            return null;        } else {            return getTileAttributeByMappedInteger(tileAttribute.getMappedInteger() - 1);        }    }    public static String getTileAttributeDescription(TileAttribute tileAttribute) {        if (bubbleTileAttributes().contains(tileAttribute)) {            return glassDescription;        } else if (blockTileAttributes().contains(tileAttribute)) {            return blockDescription;        } else {            return null;        }    }}