package com.thalhamer.numbersgame.enums.sounds;import android.media.MediaPlayer;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;import com.thalhamer.numbersgame.viewhelper.GameConstants;/** * Created by Brian on 10/16/2015. */public enum TileCountBeep {    ONE(1, R.raw.beep1), TWO(2, R.raw.beep2), THREE(3, R.raw.beep3), FOUR(4, R.raw.beep4),    FIVE(5, R.raw.beep5), SIX(6, R.raw.beep6), SEVEN(7, R.raw.beep7), EIGHT(8, R.raw.beep8),    NINE(9, R.raw.beep9);    public static final TileCountBeep MAX_BEEP = TileCountBeep.NINE;    private int soundNum;    private int resourceId;    private MediaPlayer mediaPlayer;    TileCountBeep(int soundNum, int resourceId) {        this.soundNum = soundNum;        this.resourceId = resourceId;        this.mediaPlayer = MediaPlayer.create(App.getContext(), resourceId);    }    public int getSoundNum() {        return soundNum;    }    public int getResourceId() {        return resourceId;    }    public MediaPlayer getMediaPlayer() {        return mediaPlayer;    }    public static TileCountBeep getTileCountBeepByTileCount(int tileCount) {        if (tileCount > GameConstants.MAX_NUM_OF_TILES_FOR_SCORE) {            return getTileCountBeep(GameConstants.MAX_NUM_OF_TILES_FOR_SCORE);        }        return getTileCountBeep(tileCount);    }    private static TileCountBeep getTileCountBeep(int tileCount) {        for (TileCountBeep tileCountBeep : TileCountBeep.values()) {            if (tileCountBeep.getSoundNum() == tileCount) {                return tileCountBeep;            }        }        return null;    }}