package com.thalhamer.numbersgame.services;import android.content.Context;import android.content.SharedPreferences;import com.thalhamer.numbersgame.Factory.App;import com.thalhamer.numbersgame.R;import com.thalhamer.numbersgame.domain.LevelData;import javax.inject.Singleton;/** * saved data service * <p/> * Created by Brian on 5/7/2015. */@Singletonpublic class SavedDataService {    public static final String UnderScore = "_";    private SharedPreferences sharedPref;    public SavedDataService() {        Context context = App.getContext().getApplicationContext();        sharedPref = context.getSharedPreferences(context.getString(R.string.preference_file_key),                Context.MODE_PRIVATE);    }    public boolean containsKey(String key) {        return sharedPref.contains(key);    }    public void saveKey(String key, int value) {        SharedPreferences.Editor editor = sharedPref.edit();        editor.putInt(key, value);        editor.apply();    }    public int getIntKeyValue(String key, int defaultValue) {        return sharedPref.getInt(key, defaultValue);    }    public void removeKey(String key) {        SharedPreferences.Editor editor = sharedPref.edit();        editor.remove(key);        editor.apply();    }    public String constructSectionName(Integer epic, Integer section) {        return App.getContext().getString(R.string.section) + epic + UnderScore + section;    }    public String constructSectionName(LevelData levelData) {        Integer epic = levelData.getEpic();        Integer section = levelData.getSection();        return constructSectionName(epic, section);    }    public String constructLevelName(Integer epic, Integer section, Integer level) {        return App.getContext().getString(R.string.level) + epic + UnderScore + section + UnderScore + level;    }    public String constructLevelName(LevelData levelData) {        Integer epic = levelData.getEpic();        Integer section = levelData.getSection();        Integer level = levelData.getLevel();        return constructLevelName(epic, section, level);    }}